import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";


const Dashboard = () => {
  const [role, setRole] = useState("");

  useEffect(() => {
    const storedRole = localStorage.getItem("role");
    console.log("Stored Role:", storedRole); // Debugging
    setRole(storedRole);
  }, []);
  

  const handleAddTeacher = async (teacherData) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/staff/add', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(teacherData)
      });

      const data = await response.json();
      if (response.ok) {
        alert('Teacher added successfully!');
      } else {
        alert(data.message || 'Failed to add teacher');
      }
    } catch (error) {
      console.error('Add teacher error:', error);
    }
  };


  return (
    <div>
      <h1>Admin Dashboard</h1>

      {role && role.trim() === "admin" && (
  <div>
    <button onClick={() => window.location.href = "/add-teacher"}>
      âž• Add Teacher
    </button>
    <button onClick={() => window.location.href = "/add-student"}>
      âž• Add Student
    </button>
  </div>
)}

      <button className="w-full bg-gray-500 text-white p-2 rounded" onClick={() => window.location.href = "/mark-attendance"}>
        âœ… Mark Attendance
      </button>
      <button onClick={() => window.location.href = "/view-reports"}>
        ðŸ“Š View Reports
      </button>
    </div>
  );
};

export default Dashboard;
